package com.vk2.touchsreentab.adapter;import android.arch.paging.PagedListAdapter;import android.databinding.DataBindingUtil;import android.support.annotation.NonNull;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import com.vk2.touchsreentab.R;import com.vk2.touchsreentab.adapter.viewholder.AblumViewHolder;import com.vk2.touchsreentab.databinding.ItemAlbumsBinding;import com.vk2.touchsreentab.model.Ablum;public class AblumAdapter extends PagedListAdapter<Ablum, AblumViewHolder> {    public AblumAdapter() {        super(Ablum.DIFF_CALLBACK);    }    @NonNull    @Override    public AblumViewHolder onCreateViewHolder(@NonNull ViewGroup viewGroup, int i) {        ItemAlbumsBinding binding = DataBindingUtil.inflate(LayoutInflater.from(viewGroup.getContext()), R.layout.item_albums, viewGroup, false);        return new AblumViewHolder(binding);    }    @Override    public void onBindViewHolder(@NonNull AblumViewHolder ablumViewHolder, int i) {        final  Ablum ablum = getItem(i);        if(ablum == null)return;        ablumViewHolder.albumsBinding.setAblum(ablum);        ablumViewHolder.albumsBinding.executePendingBindings();        ablumViewHolder.albumsBinding.imgAlbums.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                if (onItemClick != null) onItemClick.onItemClick(ablum);            }        });    }    public interface OnItemClick {        void onItemClick(Ablum song);    }    private OnItemClick onItemClick;    public void setOnItemClick(OnItemClick onItemClick) {        this.onItemClick = onItemClick;    }}