package com.vk2.touchsreentab.adapter;import androidx.annotation.NonNull;import androidx.databinding.DataBindingUtil;import androidx.paging.PagedListAdapter;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import com.vk2.touchsreentab.R;import com.vk2.touchsreentab.adapter.viewholder.AblumViewHolder;import com.vk2.touchsreentab.databinding.ItemAlbumsBinding;import com.vk2.touchsreentab.model.api.Album;public class AlbumAdapter extends PagedListAdapter<Album, AblumViewHolder> {    public AlbumAdapter() {        super(Album.DIFF_CALLBACK);    }    @NonNull    @Override    public AblumViewHolder onCreateViewHolder(@NonNull ViewGroup viewGroup, int i) {        ItemAlbumsBinding binding = DataBindingUtil.inflate(LayoutInflater.from(viewGroup.getContext()), R.layout.item_albums, viewGroup, false);        return new AblumViewHolder(binding);    }    @Override    public void onBindViewHolder(@NonNull AblumViewHolder ablumViewHolder, int i) {        final Album ablum = getItem(i);        if (ablum == null) return;        ablumViewHolder.albumsBinding.setAblum(ablum);        ablumViewHolder.albumsBinding.executePendingBindings();        ablumViewHolder.albumsBinding.imgAlbums.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                if (onItemClick != null) onItemClick.onItemClick(ablum);            }        });    }    public interface OnItemClick {        void onItemClick(Album song);    }    private OnItemClick onItemClick;    public void setOnItemClick(OnItemClick onItemClick) {        this.onItemClick = onItemClick;    }}