package com.vk2.touchsreentab.adapter;import android.content.Context;import android.support.annotation.NonNull;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import com.vk2.touchsreentab.R;import java.util.List;public class CategoryAdapter extends RecyclerView.Adapter<CategoryAdapter.MyViewHolder> {    private LayoutInflater inflater;    private List<String> mListCategory;    private Context mContext;    private int itemHeight;    public CategoryAdapter(Context context, List<String> list) {        mContext = context;        mListCategory = list;        inflater = LayoutInflater.from(mContext);    }    @NonNull    @Override    public CategoryAdapter.MyViewHolder onCreateViewHolder(@NonNull ViewGroup viewGroup, int i) {        View view = inflater.inflate(R.layout.item_albums, viewGroup, false);        CategoryAdapter.MyViewHolder holder = new CategoryAdapter.MyViewHolder(view);        return holder;    }    @Override    public int getItemCount() {        return mListCategory.size();    }    @Override    public void onBindViewHolder(@NonNull MyViewHolder myViewHolder, int i) {    }    class MyViewHolder extends RecyclerView.ViewHolder {        ImageView imgCategory;        public MyViewHolder(View itemView) {            super(itemView);            imgCategory = itemView.findViewById(R.id.imgArtists);        }    }}