package com.vk2.touchsreentab.fragment;import android.content.Context;import android.os.Bundle;import android.view.Display;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import androidx.annotation.NonNull;import androidx.annotation.Nullable;import com.commonsware.cwac.preso.PresentationFragment;import com.vk2.touchsreentab.R;import com.vk2.touchsreentab.activity.DualMode;public class FragmentDisplayPresentation extends PresentationFragment {    private AdsFragment adsFragment;    public static FragmentDisplayPresentation newInstance(Context ctxt, Display display) {        FragmentDisplayPresentation frag = new FragmentDisplayPresentation();        frag.setDisplay(ctxt, display);        return (frag);    }    @Override    public View onCreateView(LayoutInflater inflater, ViewGroup container, Bundle savedInstanceState) {        return inflater.inflate(R.layout.presentation_box, container, false);    }    @Override    public void onViewCreated(View view, @Nullable Bundle savedInstanceState) {        super.onViewCreated(view, savedInstanceState);        ((DualMode) getActivity()).onDefault();    }    public void showAdsFragment(@NonNull final Bundle bundle) {        hideAdsFragment();        adsFragment = AdsFragment.newInstance(bundle);        getChildFragmentManager().beginTransaction()                .add(R.id.framePlayer, adsFragment)                .commit();    }    public void hideAdsFragment() {        if (adsFragment != null && adsFragment.isAdded()) {            getChildFragmentManager().beginTransaction()                    .remove(adsFragment)                    .commit();            adsFragment = null;        }    }    public void showNormalPlayer(@NonNull Bundle bundle) {        getChildFragmentManager().beginTransaction()                .replace(R.id.framePlayer, NormalPlayer.newInstance(bundle))                .commit();    }    public void showExoPlayer(@NonNull Bundle bundle) {        getChildFragmentManager().beginTransaction()                .replace(R.id.framePlayer, ExoPlayer.newInstance(bundle))                .commit();    }    public void showYoutubePlayer(@NonNull Bundle bundle) {        getChildFragmentManager().beginTransaction()                .replace(R.id.framePlayer, YoutubePlayer.newInstance(bundle))                .commit();    }}