package com.vk2.touchsreentab.fragment;import android.content.Context;import android.os.Bundle;import android.view.Display;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import com.commonsware.cwac.preso.PresentationFragment;import com.vk2.touchsreentab.R;public class FragmentDisplayPresentation extends PresentationFragment {    public static FragmentDisplayPresentation newInstance(Context ctxt,                                                          Display display) {        FragmentDisplayPresentation frag=new FragmentDisplayPresentation();        frag.setDisplay(ctxt, display);        return(frag);    }    @Override    public View onCreateView(LayoutInflater inflater,                             ViewGroup container,                             Bundle savedInstanceState) {        View result=inflater.inflate(R.layout.presentation_box, container, false);        final FragmentBoxScreen wvf=new FragmentBoxScreen();        getChildFragmentManager().beginTransaction()                .add(R.id.nested_layout, wvf).commit();        return(result);    }}