package com.vk2.touchsreentab.fragment;import android.arch.lifecycle.Observer;import android.arch.lifecycle.ViewModelProviders;import android.os.Bundle;import android.support.annotation.NonNull;import android.support.annotation.Nullable;import android.support.v7.widget.LinearLayoutManager;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import com.vk2.touchsreentab.R;import com.vk2.touchsreentab.adapter.PlaylistAdapter;import com.vk2.touchsreentab.database.entity.Song;import com.vk2.touchsreentab.fragment.fragmentcontroller.Fragmentez;import com.vk2.touchsreentab.model.PageControl;import com.vk2.touchsreentab.model.viewmodel.PlaylistModelView;import com.vk2.touchsreentab.utils.OnSingleClickListener;import java.util.ArrayList;import java.util.List;public class PlaylistFragment extends BaseFragment {    private View view;    private int position;    private int currentPage = 1;    private int numberItem = 10;    private int totalPage = 0;    private boolean isVisible;    @Nullable    @Override    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {        view = inflater.inflate(R.layout.fragment_playlist, container, false);        initView();        showBackButton();        showTopBar();        clearTextSearch();        return view;    }    private void initView() {        final RecyclerView rcv = view.findViewById(R.id.rcvPlaylist);        final PlaylistAdapter adapter = new PlaylistAdapter(new ArrayList<Song>());        rcv.setAdapter(adapter);        final LinearLayoutManager layoutManager = new LinearLayoutManager(getActivity(), LinearLayoutManager.VERTICAL, false);        rcv.addItemDecoration(getDivider(layoutManager));        rcv.setLayoutManager(layoutManager);        rcv.setAdapter(adapter);        if (getActivity() == null) return;        getPlaylistModelView().getPlaylist().observe(getActivity(), new Observer<List<Song>>() {            @Override            public void onChanged(List<Song> songs) {                if (songs != null) {                    totalPage = (int) Math.ceil((double) songs.size() / numberItem);                    updateCurrentPage(currentPage, totalPage);                }                adapter.update(songs);            }        });        adapter.setOnItemClick(new PlaylistAdapter.OnItemClick() {            @Override            public void onItemClick(Song song) {            }            @Override            public void onRemoveClick(Song song) {                updatePlaylist(song, PlaylistModelView.REMOVE);            }            @Override            public void onMoveClick(Song song) {                updatePlaylist(song, PlaylistModelView.MOVE);            }            @Override            public void onIconClick(Song song) {            }        });        getPageControlViewModel().getPageControl().observe(getActivity(), new Observer<PageControl>() {            @Override            public void onChanged(PageControl pageControl) {                if (pageControl.getFrg() == Fragmentez.PLAYLIST_FRAGMENT) {                    if (pageControl.getDirection() == PageControl.NEXT) {                        rcv.post(new Runnable() {                            @Override                            public void run() {                                currentPage++;                                int nextPosition = currentPage * numberItem - 1;                                rcv.smoothScrollToPosition(nextPosition);                            }                        });                    } else if (pageControl.getDirection() == PageControl.PREVIOUS) {                        rcv.post(new Runnable() {                            @Override                            public void run() {                                currentPage--;                                int prevPosition = currentPage * numberItem - numberItem;                                rcv.smoothScrollToPosition(prevPosition);                            }                        });                    } else if (pageControl.getDirection() == PageControl.TOP) {                        if (position > 0) {                            rcv.post(new Runnable() {                                @Override                                public void run() {                                    currentPage = 1;                                    layoutManager.scrollToPositionWithOffset(0, 0);                                    rcv.smoothScrollToPosition(0);                                }                            });                        } else {                            gotoRecommend();                        }                    }                }            }        });        rcv.addOnScrollListener(new RecyclerView.OnScrollListener() {            @Override            public void onScrollStateChanged(@NonNull RecyclerView recyclerView, int newState) {                super.onScrollStateChanged(recyclerView, newState);                if (newState == RecyclerView.SCROLL_STATE_IDLE) {                    int p;                    LinearLayoutManager linearLayoutManager = (LinearLayoutManager) recyclerView.getLayoutManager();                    if (linearLayoutManager != null) {                        position = linearLayoutManager.findFirstVisibleItemPosition();                        if (position == 0 && isVisible) {                            showTopBar();                            isVisible = false;                        } else if (position > 0 && !isVisible) {                            hideTopBar();                            isVisible = true;                        }                        p = linearLayoutManager.findLastVisibleItemPosition();                        currentPage = p / numberItem;                        if (currentPage <= 0) return;                        int current = p % (currentPage * numberItem);                        if (current > 0) currentPage++;                        updateCurrentPage(currentPage, totalPage);                    }                }            }        });        view.findViewById(R.id.tvEmpty).setOnClickListener(new OnSingleClickListener() {            @Override            public void onSingleClick(View v) {                updatePlaylist(null, PlaylistModelView.EMPTY);                enable();            }        });        view.findViewById(R.id.tvRandom).setOnClickListener(new OnSingleClickListener() {            @Override            public void onSingleClick(View v) {                updatePlaylist(null, PlaylistModelView.RANDOM);                enable();            }        });    }    private void handleTopBar() {        if (position == 0) {            showTopBar();            isVisible = false;        } else {            hideTopBar();            isVisible = true;        }    }    @Override    protected void onResumeFragment() {        updateCurrentPage(currentPage, totalPage);        showBackButton();        clearTextSearch();        handleTopBar();    }}