package com.vk2.touchsreentab.fragment;import android.arch.lifecycle.Observer;import android.arch.lifecycle.ViewModelProviders;import android.arch.paging.PagedList;import android.os.Bundle;import android.support.annotation.NonNull;import android.support.annotation.Nullable;import android.support.v7.widget.LinearLayoutManager;import android.support.v7.widget.RecyclerView;import android.text.TextUtils;import android.util.Log;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.LinearLayout;import com.vk2.touchsreentab.R;import com.vk2.touchsreentab.adapter.MultiViewComplexAdapter;import com.vk2.touchsreentab.aplication.MyApplication;import com.vk2.touchsreentab.database.entity.Song;import com.vk2.touchsreentab.fragment.fragmentcontroller.Fragmentez;import com.vk2.touchsreentab.model.PageControl;import com.vk2.touchsreentab.model.TextSearch;import com.vk2.touchsreentab.model.viewmodel.PageControlViewModel;import com.vk2.touchsreentab.model.viewmodel.TextSearchViewModel;import com.vk2.touchsreentab.model.viewmodel.SongViewModel;public class SearchComplexFragment extends BaseFragment {    private View view;    private String search = "";    private int currentPage = 1;    private int numberItem = 6;    private int totalPage = 0;    @Nullable    @Override    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {        view = inflater.inflate(R.layout.fragment_search_complex, container, false);        initRecyclerView();        return view;    }    private void initRecyclerView() {        if (getActivity() == null) return;        final RecyclerView rcv = view.findViewById(R.id.rcv);        final MultiViewComplexAdapter adapter = new MultiViewComplexAdapter();        LinearLayoutManager layoutManager = new LinearLayoutManager(getActivity(), LinearLayout.VERTICAL, false);        rcv.setLayoutManager(layoutManager);        rcv.addItemDecoration(getDivider(layoutManager));        final SongViewModel songViewModel = ViewModelProviders.of(getActivity()).get(SongViewModel.class);        songViewModel.getSearchSong(MyApplication.appDatabase.songDao());        songViewModel.listSearchSong.observe(getActivity(), new Observer<PagedList<Song>>() {            @Override            public void onChanged(@Nullable PagedList<Song> songs) {                if (!TextUtils.isEmpty(search)) {                    if (songs != null) {                        Log.d("TAG - Search Song: ", songs.size() + "");                        totalPage = (int) Math.ceil((double) songs.size() / numberItem);                        updateCurrentPage(currentPage, totalPage);                    }                    adapter.submitList(songs);                }            }        });        rcv.setAdapter(adapter);        TextSearchViewModel textSearchViewModel = ViewModelProviders.of(getActivity()).get(TextSearchViewModel.class);        textSearchViewModel.getTextSearch().observe(getActivity(), new Observer<TextSearch>() {            @Override            public void onChanged(TextSearch textSearch) {                if (textSearch.getFrg() == Fragmentez.SEARCH_COMPLEX_FRAGMENT) {                    search = textSearch.getTextSearch();                    songViewModel.search.setValue(search);                }            }        });        final PageControlViewModel pageControlViewModel = ViewModelProviders.of(getActivity()).get(PageControlViewModel.class);        pageControlViewModel.getPageControl().observe(getActivity(), new Observer<PageControl>() {            @Override            public void onChanged(PageControl pageControl) {                if (pageControl.getFrg() == Fragmentez.SEARCH_COMPLEX_FRAGMENT) {                    if (pageControl.getDirection() == PageControl.NEXT) {                        rcv.post(new Runnable() {                            @Override                            public void run() {                                currentPage++;                                int nextPosition = currentPage * numberItem - 1;                                rcv.smoothScrollToPosition(nextPosition);                            }                        });                    } else if (pageControl.getDirection() == PageControl.PREVIOUS) {                        rcv.post(new Runnable() {                            @Override                            public void run() {                                currentPage--;                                int prevPosition = currentPage * numberItem - numberItem;                                rcv.smoothScrollToPosition(prevPosition);                            }                        });                    }                }            }        });        rcv.addOnScrollListener(new RecyclerView.OnScrollListener() {            @Override            public void onScrollStateChanged(@NonNull RecyclerView recyclerView, int newState) {                super.onScrollStateChanged(recyclerView, newState);                if (newState == RecyclerView.SCROLL_STATE_IDLE) {                    int position;                    LinearLayoutManager linearLayoutManager = (LinearLayoutManager) recyclerView.getLayoutManager();                    if (linearLayoutManager != null) {                        position = linearLayoutManager.findLastVisibleItemPosition();                        currentPage = position / numberItem;                        if (currentPage <= 0) return;                        int current = position % (currentPage * numberItem);                        if (current > 0) currentPage++;                        updateCurrentPage(currentPage, totalPage);                    }                }            }        });    }    @Override    public int getCurrentPage() {        return currentPage;    }    @Override    public int getTotalPage() {        return totalPage;    }}