package com.vk2.touchsreentab.fragment;import android.arch.lifecycle.Observer;import android.arch.lifecycle.ViewModelProviders;import android.arch.paging.PagedList;import android.os.Bundle;import android.support.annotation.NonNull;import android.support.annotation.Nullable;import android.support.v7.widget.DividerItemDecoration;import android.support.v7.widget.LinearLayoutManager;import android.support.v7.widget.RecyclerView;import android.util.Log;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.LinearLayout;import com.vk2.touchsreentab.R;import com.vk2.touchsreentab.adapter.MultiViewAdapter;import com.vk2.touchsreentab.aplication.MyApplication;import com.vk2.touchsreentab.database.entity.Song;import com.vk2.touchsreentab.model.viewmodel.SongViewModel;public class SearchComplexFragment extends BaseFragment {    private View view;    @Nullable    @Override    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {        view = inflater.inflate(R.layout.fragment_search_complex, container, false);        initRecyclerView();        return view;    }    private void initRecyclerView() {        if (getActivity() == null) return;        RecyclerView rcv = view.findViewById(R.id.rcv);        final MultiViewAdapter adapter = new MultiViewAdapter();        LinearLayoutManager layoutManager = new LinearLayoutManager(getActivity(), LinearLayout.VERTICAL, false);        rcv.setLayoutManager(layoutManager);        DividerItemDecoration dividerItemDecoration = new DividerItemDecoration(getActivity(), layoutManager.getOrientation());        dividerItemDecoration.setDrawable(getActivity().getResources().getDrawable(R.drawable.divider));        rcv.addItemDecoration(dividerItemDecoration);        SongViewModel songViewModel = ViewModelProviders.of(getActivity()).get(SongViewModel.class);        songViewModel.search.setValue("");        songViewModel.getSongData(MyApplication.appDatabase.songDao());        songViewModel.listSong.observe(getActivity(), new Observer<PagedList<Song>>() {            @Override            public void onChanged(@Nullable PagedList<Song> songs) {                Log.d("TAG", "Song Data");                adapter.submitList(songs);            }        });        rcv.setAdapter(adapter);    }}