package com.vk2.touchsreentab.fragment;import android.arch.lifecycle.Observer;import android.arch.lifecycle.ViewModelProviders;import android.arch.paging.PagedList;import android.os.Bundle;import android.support.annotation.NonNull;import android.support.annotation.Nullable;import android.support.v7.widget.LinearLayoutManager;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.LinearLayout;import com.vk2.touchsreentab.R;import com.vk2.touchsreentab.adapter.MultiViewSingerAdapter;import com.vk2.touchsreentab.adapter.SongAdapter;import com.vk2.touchsreentab.aplication.MyApplication;import com.vk2.touchsreentab.database.entity.Song;import com.vk2.touchsreentab.model.viewmodel.SingerVewModel;import com.vk2.touchsreentab.model.viewmodel.SongViewModel;import java.util.ArrayList;import java.util.List;public class SingerFragment extends BaseFragment {    private View view;    @Nullable    @Override    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {        view = inflater.inflate(R.layout.fragment_singer, container, false);        initSong();        return view;    }    private void initSong() {        final RecyclerView recyclerView = view.findViewById(R.id.rcv);        final MultiViewSingerAdapter adapter = new MultiViewSingerAdapter();        recyclerView.setAdapter(adapter);        LinearLayoutManager layoutManager = new LinearLayoutManager(getActivity(), LinearLayout.VERTICAL, false);        recyclerView.setLayoutManager(layoutManager);        recyclerView.addItemDecoration(getDivider(layoutManager));        if (getActivity() == null) return;        SingerVewModel singerViewModel = ViewModelProviders.of(getActivity()).get(SingerVewModel.class);        singerViewModel.listSongBySinger.observe(getActivity(), new Observer<PagedList<Song>>() {            @Override            public void onChanged(@Nullable PagedList<Song> songs) {                if (songs != null) {                }                adapter.submitList(songs);            }        });    }}