package com.vk2.touchsreentab.model;import android.databinding.BindingAdapter;import android.support.annotation.NonNull;import android.support.v7.util.DiffUtil;import android.widget.ImageView;import com.bumptech.glide.Glide;public class Ablum {    private String name;    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public Integer getId() {        return id;    }    public void setId(Integer id) {        this.id = id;    }    public Integer getType() {        return type;    }    public void setType(Integer type) {        this.type = type;    }    public String getImagePath() {        return imagePath;    }    public void setImagePath(String imagePath) {        this.imagePath = imagePath;    }    private Integer id;    private Integer type;    private String imagePath = "https://api.androidhive.info/images/nature/5.jpg";    @BindingAdapter("urlSongImage")    public static void loadImage(ImageView view, String imageUrl) {        Glide.with(view.getContext())                .load(imageUrl)                .into(view);    }    public static DiffUtil.ItemCallback<Ablum> DIFF_CALLBACK = new DiffUtil.ItemCallback<Ablum>() {        @Override        public boolean areItemsTheSame(@NonNull Ablum ablum, @NonNull Ablum t1) {            return ablum.id.equals(t1.id);        }        @Override        public boolean areContentsTheSame(@NonNull Ablum ablum, @NonNull Ablum t1) {            return ablum.equals(t1);        }    };    @Override    public boolean equals(Object obj) {        if (obj == this) return true;        Ablum singer = (Ablum) obj;        return singer.id.equals(this.id);    }}