package com.vk2.touchsreentab.model;import android.arch.lifecycle.MutableLiveData;import android.arch.paging.DataSource;public class SingerDataSourceFactory extends DataSource.Factory {    SingerDataSource singerDataSource;    MutableLiveData<SingerDataSource> mutableLiveData;    public SingerDataSourceFactory(){        mutableLiveData = new MutableLiveData<>();    }    @Override    public DataSource create() {        singerDataSource = new SingerDataSource();        mutableLiveData.postValue(singerDataSource);        return singerDataSource;    }    public MutableLiveData<SingerDataSource> getMutableLiveData() {        return mutableLiveData;    }}