package com.vk2.touchsreentab.model;import android.arch.lifecycle.MutableLiveData;import android.arch.paging.DataSource;public class SongDataSourceFactory extends DataSource.Factory {    SongDataSource songDataSource;    MutableLiveData<SongDataSource> mutableLiveData;    public SongDataSourceFactory(){        mutableLiveData = new MutableLiveData<>();    }    @Override    public DataSource create() {        songDataSource = new SongDataSource();        mutableLiveData.postValue(songDataSource);        return songDataSource;    }    public MutableLiveData<SongDataSource> getMutableLiveData() {        return mutableLiveData;    }}