package com.vk2.touchsreentab.model.api;import androidx.annotation.NonNull;import androidx.databinding.BindingAdapter;import androidx.recyclerview.widget.DiffUtil;import android.widget.ImageView;import com.bumptech.glide.Glide;import com.bumptech.glide.load.resource.bitmap.RoundedCorners;import com.bumptech.glide.request.RequestOptions;public class Album {    private String name;    private String value;    public String getValue() {        return value;    }    public void setValue(String value) {        this.value = value;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public Integer getId() {        return id;    }    public void setId(Integer id) {        this.id = id;    }    public Integer getType() {        return type;    }    public void setType(Integer type) {        this.type = type;    }    public String getImagePath() {        return imagePath;    }    public void setImagePath(String imagePath) {        this.imagePath = imagePath;    }    private Integer id;    private Integer type;    private String imagePath;    @BindingAdapter("urlAlbumImage")    public static void loadImage(ImageView view, String imageUrl) {        Glide.with(view.getContext())                .load(imageUrl)                .apply(RequestOptions.bitmapTransform(new RoundedCorners(4)))                .into(view);    }    public static DiffUtil.ItemCallback<Album> DIFF_CALLBACK = new DiffUtil.ItemCallback<Album>() {        @Override        public boolean areItemsTheSame(@NonNull Album ablum, @NonNull Album t1) {            return ablum.id.equals(t1.id);        }        @Override        public boolean areContentsTheSame(@NonNull Album ablum, @NonNull Album t1) {            return ablum.equals(t1);        }    };    @Override    public boolean equals(Object obj) {        if (obj == this) return true;        Album singer = (Album) obj;        return singer.id.equals(this.id);    }}