package com.vk2.touchsreentab.model.datasource;import android.arch.lifecycle.MutableLiveData;import android.arch.paging.DataSource;public class AblumDataSourceFactory extends DataSource.Factory {    AblumDataSource ablumDataSource;    MutableLiveData<AblumDataSource> mutableLiveData;    public AblumDataSourceFactory(){        mutableLiveData = new MutableLiveData<>();    }    @Override    public DataSource create() {        ablumDataSource = new AblumDataSource();        mutableLiveData.postValue(ablumDataSource);        return ablumDataSource;    }    public MutableLiveData<AblumDataSource> getMutableLiveData() {        return mutableLiveData;    }}