package com.vk2.touchsreentab.model.datasource;import android.content.Context;import androidx.lifecycle.MutableLiveData;import androidx.paging.DataSource;public class AlbumDataSourceFactory extends DataSource.Factory {    private Context context;    private MutableLiveData<AlbumDataSource> mutableLiveData;    public AlbumDataSourceFactory(Context context) {        this.context = context;        mutableLiveData = new MutableLiveData<>();    }    @Override    public DataSource create() {        AlbumDataSource albumDataSource = new AlbumDataSource(context);        mutableLiveData.postValue(albumDataSource);        return albumDataSource;    }}