package com.vk2.touchsreentab.model.datasource;import android.content.Context;import androidx.annotation.NonNull;import androidx.paging.PageKeyedDataSource;import com.vk2.touchsreentab.api.ApiController;import com.vk2.touchsreentab.api.Callback;import com.vk2.touchsreentab.database.entity.Singer;import com.vk2.touchsreentab.model.api.SingerForm;import java.util.List;public class SingerDataSource extends PageKeyedDataSource<Integer, Singer> {    private Context context;    private int page = 1, limit = 15;    SingerDataSource(Context context) {        this.context = context;    }    @Override    public void loadInitial(@NonNull LoadInitialParams<Integer> params, @NonNull final LoadInitialCallback<Integer, Singer> callback) {        ApiController.getInstance().getHotSingers(context, page, limit, new Callback() {            @Override            public void response(Object obj) {                try {                    SingerForm singerForm = (SingerForm) obj;                    if (singerForm != null) {                        if (singerForm.getErr() == ApiController.SUCCESS) {                            List<Singer> singers = singerForm.getData();                            if (singers != null) {                                callback.onResult(singers, page--, page++);                                page++;                            }                        }                    }                } catch (ClassCastException e) {                    e.printStackTrace();                }            }        });    }    @Override    public void loadBefore(@NonNull LoadParams<Integer> params, @NonNull LoadCallback<Integer, Singer> callback) {    }    @Override    public void loadAfter(@NonNull LoadParams<Integer> params, @NonNull final LoadCallback<Integer, Singer> callback) {        ApiController.getInstance().getHotSingers(context, page, limit, new Callback() {            @Override            public void response(Object obj) {                try {                    SingerForm singerForm = (SingerForm) obj;                    if (singerForm != null) {                        if (singerForm.getErr() == ApiController.SUCCESS) {                            List<Singer> singers = singerForm.getData();                            if (singers != null && singers.size() > 0) {                                callback.onResult(singers, page);                                page++;                            }                        }                    }                } catch (ClassCastException e) {                    e.printStackTrace();                }            }        });    }}