package com.vk2.touchsreentab.model.datasource;import android.content.Context;import androidx.lifecycle.MutableLiveData;import androidx.paging.DataSource;public class SingerDataSourceFactory extends DataSource.Factory {    private Context context;    private MutableLiveData<SingerDataSource> mutableLiveData;    public SingerDataSourceFactory(Context context) {        mutableLiveData = new MutableLiveData<>();        this.context = context;    }    @Override    public DataSource create() {        SingerDataSource singerDataSource = new SingerDataSource(context);        mutableLiveData.postValue(singerDataSource);        return singerDataSource;    }}