package com.vk2.touchsreentab.model.datasource;import android.content.Context;import androidx.lifecycle.MutableLiveData;import androidx.paging.DataSource;public class SongDataSourceFactory extends DataSource.Factory {    private Context context;    private MutableLiveData<SongDataSource> mutableLiveData;    public SongDataSourceFactory(Context context){        this.context = context;        mutableLiveData = new MutableLiveData<>();    }    @Override    public DataSource create() {        SongDataSource songDataSource = new SongDataSource(context);        mutableLiveData.postValue(songDataSource);        return songDataSource;    }    public MutableLiveData<SongDataSource> getMutableLiveData() {        return mutableLiveData;    }}