package com.vk2.touchsreentab.model.viewmodel;import android.arch.lifecycle.LiveData;import android.arch.lifecycle.ViewModel;import android.arch.paging.LivePagedListBuilder;import android.arch.paging.PagedList;import com.vk2.touchsreentab.model.Ablum;import com.vk2.touchsreentab.model.datasource.AblumDataSourceFactory;public class AblumViewModel  extends ViewModel {    public LiveData<PagedList<Ablum>> listAblumOnline;    public void getAllListAblum() {        AblumDataSourceFactory ablumDataSourceFactory = new AblumDataSourceFactory();        PagedList.Config pagedListConfig =                (new PagedList.Config.Builder()).setEnablePlaceholders(false)                        .setInitialLoadSizeHint(10)                        .setPageSize(10).build();        listAblumOnline = new LivePagedListBuilder(ablumDataSourceFactory, pagedListConfig).build();    }}