package com.vk2.touchsreentab.model.viewmodel;import android.content.Context;import androidx.lifecycle.LiveData;import androidx.lifecycle.ViewModel;import androidx.paging.LivePagedListBuilder;import androidx.paging.PagedList;import com.vk2.touchsreentab.model.api.Album;import com.vk2.touchsreentab.model.datasource.AlbumDataSourceFactory;public class AlbumViewModel extends ViewModel {    public LiveData<PagedList<Album>> listAlbumOnline;    @SuppressWarnings("unchecked")    public void getHotAlbums(Context context) {        AlbumDataSourceFactory albumDataSourceFactory = new AlbumDataSourceFactory(context);        PagedList.Config pagedListConfig =                (new PagedList.Config.Builder()).setEnablePlaceholders(false)                        .setInitialLoadSizeHint(15)                        .setPageSize(15).build();        listAlbumOnline = new LivePagedListBuilder(albumDataSourceFactory, pagedListConfig).build();    }}