package com.vk2.touchsreentab.utils;import android.content.Context;import android.content.SharedPreferences;public class SaveDataUtils {    private static SaveDataUtils saveData = null;    private final SharedPreferences sharePreference;    private SaveDataUtils(Context context) {        sharePreference = context.getSharedPreferences("touchscreen", Context.MODE_PRIVATE);    }    public static SaveDataUtils getInstance(Context context) {        if (saveData == null) {            saveData = new SaveDataUtils(context);        }        return saveData;    }    public void setKeyYouTube(String keyYouTube) {        sharePreference.edit().putString("KeyYouTube", keyYouTube).apply();    }    public String getKeyYouTube() {        return sharePreference.getString("KeyYouTube", "AIzaSyAeUyi3q4lB21g18hUnR9NWpqU8eTDYsl8");    }    public void setKeySoundCloud(String keyYouTube) {        sharePreference.edit().putString("KeySoundCloud", keyYouTube).apply();    }    public String getKeySoundCloud() {        return sharePreference.getString("KeySoundCloud", "dec73200bd1c369969eb9df50f4a190b");    }    public void setApiToken(String token) {        sharePreference.edit().putString("ApiToken", token).apply();    }    public String getApiToken() {        return sharePreference.getString("ApiToken", "");    }    public void setLanguageSetting(int position) {        sharePreference.edit().putInt("LanguageSetting", position).apply();    }    public int getLanguageSetting() {        return sharePreference.getInt("LanguageSetting", 3);    }}